-- Table: ad_impressions
DROP TABLE IF EXISTS `ad_impressions`;
CREATE TABLE `ad_impressions` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `ad_id` bigint unsigned NOT NULL,
  `ad_creative_id` bigint unsigned NOT NULL,
  `placement` varchar(64) COLLATE utf8mb4_unicode_ci NOT NULL,
  `user_id` bigint unsigned DEFAULT NULL,
  `session_id` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `ip_address` varchar(45) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `user_agent` text COLLATE utf8mb4_unicode_ci,
  `referrer` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `viewed_at` timestamp NOT NULL,
  `metadata` json DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `ad_impressions_ad_creative_id_foreign` (`ad_creative_id`),
  KEY `ad_impressions_ad_id_viewed_at_index` (`ad_id`,`viewed_at`),
  KEY `ad_impressions_user_id_viewed_at_index` (`user_id`,`viewed_at`),
  KEY `ad_impressions_placement_viewed_at_index` (`placement`,`viewed_at`),
  KEY `ad_impressions_viewed_at_index` (`viewed_at`),
  CONSTRAINT `ad_impressions_ad_creative_id_foreign` FOREIGN KEY (`ad_creative_id`) REFERENCES `ad_creatives` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `ad_impressions_ad_id_foreign` FOREIGN KEY (`ad_id`) REFERENCES `ads` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `ad_impressions_user_id_foreign` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE SET NULL ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=1061 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

