-- Table: ad_reports
DROP TABLE IF EXISTS `ad_reports`;
CREATE TABLE `ad_reports` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `ad_id` bigint unsigned DEFAULT NULL,
  `campaign_id` bigint unsigned DEFAULT NULL,
  `placement` varchar(64) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `report_date` date NOT NULL,
  `report_type` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'daily',
  `impressions` bigint unsigned NOT NULL DEFAULT '0',
  `clicks` bigint unsigned NOT NULL DEFAULT '0',
  `spend` bigint unsigned NOT NULL DEFAULT '0',
  `ctr` decimal(5,4) DEFAULT NULL,
  `cpm` bigint unsigned DEFAULT NULL,
  `cpc` bigint unsigned DEFAULT NULL,
  `metadata` json DEFAULT NULL,
  `generated_at` timestamp NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `ad_reports_ad_id_report_date_index` (`ad_id`,`report_date`),
  KEY `ad_reports_campaign_id_report_date_index` (`campaign_id`,`report_date`),
  KEY `ad_reports_report_date_index` (`report_date`),
  KEY `ad_reports_report_type_index` (`report_type`),
  CONSTRAINT `ad_reports_ad_id_foreign` FOREIGN KEY (`ad_id`) REFERENCES `ads` (`id`) ON DELETE SET NULL ON UPDATE CASCADE,
  CONSTRAINT `ad_reports_campaign_id_foreign` FOREIGN KEY (`campaign_id`) REFERENCES `ad_campaigns` (`id`) ON DELETE SET NULL ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

